import { useEffect, useState } from "react";

function App() {
  const [city, setCity] = useState(null);
  const [search, setSearch] = useState("mumbai");

  useEffect(() => {
    const timer = setTimeout(() => {
      fetchApi();
    }, 500); // Delay in milliseconds

    return () => clearTimeout(timer); // Clean up the timer if the component unmounts or the search value changes
  }, [search]);

  const fetchApi = async () => {
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=9874c795da33890b6e3ab81507512c0a`;
    const response = await fetch(url);
    const resJson = await response.json();
    setCity(resJson.main);
  };

  return (
    <>
      <div className="box" style={{}}>
        <div className="search">
          <input
            type="search"
            className="inpit-field"
            onChange={(event) => {
              setSearch(event.target.value);
            }}
          />
        </div>
        <div className="info">
          <h2 className="location">{search}</h2>
          <h3 className="temperature">Temperature</h3>
          <h3 className="min-max">min : 12 C || max : 35 C</h3>
        </div>
      </div>
    </>
  );
}

export default App;
