import { useEffect, useState } from "react";


function App() {
  const [city , setCity] = useState(null);
  const [search , setSearch] = useState("mumbai");

  useEffect( () =>{
    const fetchApi = async() =>{
      const url = 'https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=9874c795da33890b6e3ab81507512c0a';
      const response = await fetch(url);
      const resJson = await response.json();
      setCity(resJson.main);
    };
    fetchApi();
  },[search])


  return (
    <>
      <div className="box" style={{}}>

        <div className="search" >

          <input
            type="search"
            className="inpit-field"
            onChange={(event) => {
              setSearch(event.target.value)}} />
        </div>
        <div className="info">
          <h2 className="location">

            {search}

          </h2>
          <h3 className="temperature">
            {city.main.temp}

          </h3>
          <h3 className="min-max">min : {city.main.temp_min} || max : {city.main.temp_max}</h3>
        </div>
      </div>
    </>

  );
}

export default App;
