import { useEffect, useState } from "react";

function App() {
  const [city, setCity] = useState(null);
  const [search, setSearch] = useState("mumbai");
  const [typingTimeout, setTypingTimeout] = useState(null);

  useEffect(() => {
    const fetchApi = async () => {
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=9874c795da33890b6e3ab81507512c0a`;
      const response = await fetch(url);
      const resJson = await response.json();
      setCity(resJson.main);
    };

    if (typingTimeout) {
      clearTimeout(typingTimeout);
    }

    const timeout = setTimeout(() => {
      fetchApi();
    }, 500);

    setTypingTimeout(timeout);
  }, [search]);

  return (
    <>
      <div className="box" style={{ display: "flex", justifyContent: "center", border: "1px solid black", padding: "100px",margin : "200px",height: "50%", backgroundColor:"blue" }}>
        <div className="search">
          <input
            type="search"
            className="input-field"
            onChange={(event) => {
              setSearch(event.target.value);
            }}
          />

          {!city ? (
            <p className="errormsg">No Data Found</p>
          ) : (
            <div>
              <div className="info">
                <h2 className="location">
                  <i className="fa-solid fa-temperature-high">{search}</i>
                </h2>
                <h3 className="temperature">Temperature</h3>
                <h3 className="min-max">min : 12 C || max : 35 C</h3>
              </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
}

export default App;
